  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'doc6',
	title: 'Build Your Own CAO',
	sidebar_label: 'Build Your Own CAO'
};
export const metadata = {
  "id": "doc6",
  "isDocsHomePage": false,
  "title": "Build Your Own CAO",
  "description": "Overview",
  "source": "@site/docs/doc6.md",
