  /* @jsx mdx */

export const rightToc = [{
  value: 'Overview',
  id: 'overview',
  children: []
}, {
  value: 'Prerequisites',
  id: 'prerequisites',
  children: []
}, {
  value: 'DAO',
  id: 'dao',
  children: []
}, {
  value: 'Cooperative',
  id: 'cooperative',
  children: []
}, {
  value: 'Getting help',
  id: 'getting-help',
  children: []
}];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component '" + name + "' was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h2 {...{
      "id": "overview"
    }}>{`Overview`}</h2>
    <p>{`This page will walk you through how to deploy your own Cooperative Autonomous Organization.`}</p>
    <h2 {...{
      "id": "prerequisites"
    }}>{`Prerequisites`}</h2>
    <ul>
      <li parentName="ul">{`A name and mission statement for your CAO.`}</li>
      <li parentName="ul">{`An Ethereum wallet.`}</li>
      <li parentName="ul">{`At least 0.1 `}<inlineCode parentName="li">{`ETH`}</inlineCode>{` (likely 0.3 or more).`}</li>
      <li parentName="ul">{`A physical `}<inlineCode parentName="li">{`$25`}</inlineCode>{` check payable to Wyoming Secretary of State.`}</li>
      <li parentName="ul">{`A Wyoming physical address or registered agent.`}</li>
      <li parentName="ul">{`A few hours of time.`}</li>
    </ul>
    <blockquote>
      <p parentName="blockquote">{`You might also need $200+ to pay the IRS for a 501c3 designation.`}</p>
    </blockquote>
    <h2 {...{
      "id": "dao"
    }}>{`DAO`}</h2>
    <p>{`First go to the `}<a parentName="p" {...{
        "href": "https://mainnet.aragon.org/#/create"
      }}>{`Aragon DAO creation page`}</a>{` and select the Membership template.`}</p>
    <p>{`Choose your DAO's name as well as voting parameters. For voting we recommend 10% quorum (how many people have to show up to vote) and a 60% approval threshold (of the people who show up, how many have to vote yes for a proposal to pass). Then deploy your DAO (at the time of writing this costs between 0.1 and 0.3 `}<inlineCode parentName="p">{`ETH`}</inlineCode>{` depending on the gas fees).`}</p>
    <h2 {...{
      "id": "cooperative"
    }}>{`Cooperative`}</h2>
    <p>{`The first step is to create the `}<a parentName="p" {...{
        "href": "https://github.com/burrrata/crypto-comix/blob/master/juris/output.md"
      }}>{`articles of incorporation`}</a>{` for your cooperative. Open `}<a parentName="p" {...{
        "href": "https://github.com/burrrata/crypto-comix/blob/master/juris/output.md"
      }}>{`this file`}</a>{` and copy the text into a text editor that can export PDF documents. Then search and replace everything in `}<inlineCode parentName="p">{`{{ }}`}</inlineCode>{` with things specific to your cooperative:`}</p>
    <ul>
      <li parentName="ul"><inlineCode parentName="li">{`{{NAME}}`}</inlineCode>{` => The name of your organization.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`{{PURPOSE}}`}</inlineCode>{` => The mission statement of your organization.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`{{TOKEN}}`}</inlineCode>{` => The membership token of your DAO.`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`{{DAO-ADDRESS}}`}</inlineCode>{` => The Ethereum address of your DAO (not the ENS name, but the organization address).`}</li>
    </ul>
    <p>{`Once you've searched and replaced, then export the document as a PDF.`}</p>
    <p>{`The next step is to register your cooperative in the state of Wyoming. To do this you will need to print your PDF. You'll also need to print out `}<a parentName="p" {...{
        "href": "https://sos.wyo.gov/Forms/Business/NP/NP-ArticlesIncorporation.pdf"
      }}>{`this form`}</a>{` and fill it out. Then mail both, along with a `}<inlineCode parentName="p">{`$25`}</inlineCode>{` check, to:`}</p>
    <pre><code parentName="pre" {...{}}>{`Wyoming Secretary of State
Herschler Building East, Suite 101 122 W 25th Street Cheyenne, WY 82002-0020
`}</code></pre>
    <p>{`If you need a registered agent the following services may be helpful:`}</p>
    <blockquote>
      <p parentName="blockquote">{`We are not affiliated with the following sites nor have we reviewed them in any way.`}</p>
    </blockquote>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.bestwyomingregisteredagent.com/"
        }}>{`https://www.bestwyomingregisteredagent.com/`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.incfile.com/form/incorporate-wyoming"
        }}>{`https://www.incfile.com/form/incorporate-wyoming`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://www.wyomingagents.com/"
        }}>{`https://www.wyomingagents.com/`}</a></li>
    </ul>
    <h2 {...{
      "id": "getting-help"
    }}>{`Getting help`}</h2>
    <p>{`If you need help deploying your DAO, filling out your articles of incorporation, or registering your entity you can reach out to the `}<a parentName="p" {...{
        "href": "https://daoshop.raidguild.org/"
      }}>{`RaidGuild DAOshop`}</a>{` to set up a consultation. They'll be able to answer your questions and help you get started.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
