  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	id: 'doc7',
	title: 'Open Questions',
	sidebar_label: 'Open Questions'
};
export const metadata = {
  "id": "doc7",
  "isDocsHomePage": false,
  "title": "Open Questions",
  "description": "Can cooperatives have permissioned membership, or do they have to be open to the public?",
  "source": "@site/docs/doc7.md",
  "permalink": "/crypto-comix/docs/doc7",
  "editUrl": "https://github.com/burrrata/crypto-comix/edit/master/website/docs/doc7.md",
  "sidebar_label": "Open Questions",
  "sidebar": "someSidebar",
  "previous": {
    "title": "Build Your Own CAO",
    "permalink": "/crypto-comix/docs/doc6"
  }
};
  /* @jsx mdx */

export const rightToc = [];
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component '" + name + "' was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  rightToc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <blockquote>
      <p parentName="blockquote">{`Can cooperatives have permissioned membership, or do they have to be open to the public?`}</p>
    </blockquote>
    <ul>
      <li parentName="ul"><strong parentName="li">{`If permissioned:`}</strong>{` Members can join the cooperative by submitting a request to join along with 100 DAI. Current members will then vote to approve or deny the new member. Members can also submit proposals for work to be done and decisions to be made.`}</li>
      <li parentName="ul"><strong parentName="li">{`If public:`}</strong>{` Members can join the cooperative by paying a membership fee of 100 DAI. A current member will need to process their request on-chain (similar to how an REI employee processes a new membership request) before they can join.`}</li>
    </ul>
    <blockquote>
      <p parentName="blockquote">{`Are there requirements as to the identity and/or legal status of members? Do they have to verify that they live in-state or anything?`}</p>
    </blockquote>
    <ul>
      <li parentName="ul"><strong parentName="li">{`If no state ID is required:`}</strong>{` New members will need to provide a proof of uniqueness to verify that they are, indeed, a real person. This might look like a vote of approval from a current member, a Web3 identity profile (3Box, BrightID, Upala, etc), or a social media account such as GitHub or Twitter.`}</li>
      <li parentName="ul"><strong parentName="li">{`If state ID is required:`}</strong>{` We'll need to integrate with KYC provider or something. How do cooperatives do this currently?`}</li>
    </ul>
    <blockquote>
      <p parentName="blockquote">{`What is actually required to create a CAO legal template?`}</p>
    </blockquote>
    <ul>
      <li parentName="ul">{`Does the `}<a parentName="li" {...{
          "href": "https://getjuris.com/"
        }}>{`Juris`}</a>{` software have an API we can plug into?`}</li>
      <li parentName="ul">{`Will the software mail the forms to the state, or do users need to do that themselves?`}</li>
    </ul>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
